var osc = require('node-osc');
var client;

var value = 0.00001;

module.exports = {
	connect: function(host, port) {
		client = new osc.Client(host, port);
	},
	transmitFaderData: function (faderNumber, floatValue) {
		value = value + 0.001;
		client.send('/1/fader1', value);
		client.send('/midi', { type: 'm', channel: 0, status: 0x64, data1: 1, data2: 0xb0 } );
	}
}